n                <item name=\"level_3\"   desc=\">1500\"  id=\"4\" points=\"77.30\"  falsecontinue=\"continue\" truecontinue=\"break\" parentfalsecontinue=\"continue\"  start=\"true\">\r\n                    <exp><![CDATA[if(UcBasicInfo_rent > 1500 ) {return true;} else {return false ;}  ]]></exp>\r\n                    <params>UcBasicInfo_rent</params>\r\n                </item>\r\n                <next id=\"5\" >\r\n                    <exp><![CDATA[ return true; ]]> </exp>\r\n                    <params>UcBasicInfo_rent</params>\r\n				</next>\r\n            </group>\r\n\r\n            \r\n            <!--所属行业 -->\r\n            <group name=\"indestryGroup\"   desc=\"所属行业\"  gid=\"5\"  start=\"\" points=\"0\" >\r\n                <item name=\"type1\"   desc=\"农/林/牧/渔、IT/通信、科/教/文/卫、国家机构\"  id=\"1\"  points=\"49.26\" falsecontinue=\"continue\" truecontinue=\"break\" parentfalsecontinue=\"continue\"  start=\"true\">\r\n                    <exp>if(UcBasicInfo_industry == \"1\" ||UcBasicInfo_industry == \"7\" || UcBasicInfo_industry == \"10\" || UcBasicInfo_industry == \"11\") \r\n                    {return true;} else {return false ;} </exp>\r\n                    <params>UcBasicInfo_industry</params>\r\n                    <next id=\"2\" >\r\n                        <exp><![CDATA[ return true; ]]> </exp>\r\n                        <params>UcBasicInfo_industry</params>\r\n                    </next>\r\n                </item>\r\n                <item name=\"type2\"   desc=\"居民/商务/专业技术服务、金融、其他行业\"  id=\"2\"  points=\"38.74\" falsecontinue=\"continue\" truecontinue=\"break\" parentfalsecontinue=\"continue\"  start=\"true\">\r\n                    <exp>if(UcBasicInfo_industry == \"6\" ||UcBasicInfo_industry == \"8\" || UcBasicInfo_industry == \"12\" ) \r\n                    {return true;} else {return false ;} </exp>\r\n                    <params>UcBasicInfo_industry</params>\r\n                    <next id=\"3\" >\r\n                        <exp><![CDATA[ return tru