INSERT INTO `risk_model` VALUES ('5', '积金贷准入模型', '0', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r\n<!-- 积金贷准入模型 -->\r\n<riskmodel id=\"5\">\r\n    <orders>\r\n        <!--年龄限制 男性 21-60 周岁    女性 21-55周岁  -->\r\n        <item name=\"Age\"   desc=\"年龄限制\"  id=\"1\"  order=\"1\" refuse=\"true\" points=\"0\" refuseReason=\"43\" >\r\n            <exp><![CDATA[ if(( UcBasicInfo_gender == 1 &&  (UcBasicInfo_age < 21 ||  UcBasicInfo_age > 60))  \r\n            || ( UcBasicInfo_gender == 2 &&  (UcBasicInfo_age < 21 ||  UcBasicInfo_age > 55) )) \r\n            { return true ; } else {return false ;}]]></exp>\r\n            <params>UcBasicInfo_age,UcBasicInfo_gender</params>\r\n            <paramNames>UcBasicInfo_age=年龄,UcBasicInfo_gender=性别</paramNames>\r\n        </item>\r\n        \r\n        <!--缴纳单位与申请表填写的工作单位不一致的  -->\r\n        <item name=\"payCompany\"   desc=\"缴纳单位与申请表填写的工作单位不一致的\"  id=\"2\"  order=\"2\" refuse=\"false\" points=\"0\" >\r\n            <exp><![CDATA[ if(  UsUserDetail_company == null ||  UcFactorHouseFund_payCompany == null || !UcFactorHouseFund_payCompany.contains(UsUserDetail_company)   ) \r\n            { return true ; } else {return false ;}]]></exp>\r\n            <params>UsUserDetail_company,UcFactorHouseFund_payCompany</params>\r\n            <paramNames>UsUserDetail_company=申请表填写工作单位,UcFactorHouseFund_payCompany=公积金缴纳单位</paramNames>\r\n        </item>\r\n\r\n         <!--同一单位缴纳基数  -->\r\n        <item name=\"payBasic\"   desc=\"同一单位缴纳基数\"  id=\"3\"  order=\"3\" refuse=\"false\" points=\"0\">\r\n            <exp><![CDATA[ \r\n            boolean isHit = false;\r\n            if(UsUserDetail_city != null && UcPersonInfo_houseFundBase != null &&( UsUserDetail_city.contains(\"北京\")||UsUserDetail_city.contains(\"上海\")\r\n				||UsUserDetail_city.contains(\"广州\")||UsUserDetail_city.contains(\"深圳\") \r\n				)){\r\n                if(UcPersonInfo_houseFundBase != null &&  UcPersonInfo_houseFundBase < 3500){\r\n                    isHit = true;\r\n      